mapper.update:
UPDATE Board

	title = '${title}',
	content = '${content}',
	boardType = ${boardType}


	id=${id}

mapper.select:
SELECT board.*, (SELECT COUNT(*) FROM FileInfo WHERE boardId = board.id) AS fileCnt, (SELECT COUNT(*) FROM PhotoInfo WHERE boardId = board.id) AS pictureCnt, (SELECT title FROM BoardType WHERE id = board.boardType) AS boardName, (SELECT COUNT(*) FROM Reply WHERE boardId = board.id) AS replyCnt, usr.nickname AS writerName, (SELECT url FROM PhotoInfo where boardId = board.id LIMIT 0,1) AS thumbnail FROM Board board LEFT JOIN users usr ON (board.writer = usr.kakaoId)
( board.boardType = 1 or board.boardType >= 5 )
board.boardType = ${boardType}
and ( board.title like '%${query}%' or board.content like '%${query}%' )
order by board.id desc LIMIT ${from}, ${pageSize}
