mapper.update:
UPDATE products
menuId = ${menuId}, name = '${name}', primaryId = '${primaryId}', size = '${size}', packaging = '${packaging}', delivery = '${delivery}'
id=${id}

mapper.select:
SELECT p.*, menu.name AS menuName FROM products p LEFT JOIN menus menu ON (p.menuId = menu.id)
p.menuId = ${menuId}
and ( p.name like '%${query}%' or p.primaryId like '%${query}%' )
order by p.id desc LIMIT ${from}, ${pageSize}
