mapper.select:
SELECT d.id, d.content, d.writer, d.lang, d.symposiumId, d.stype, u.username AS username, t.title AS typeTitle, t.msgKey AS msgKey, s.title AS sympTitle FROM SymposiumDetail d LEFT JOIN SymposiumTypes t on d.stype = t.id LEFT JOIN Symposium s on d.symposiumId = s.id LEFT JOIN users u on d.writer = u.id
d.lang = '${lang}' and d.stype = ${stype} and d.symposiumId = ${symposiumId}
ORDER BY d.id DESC LIMIT 0, 1

mapper.delete:
DELETE FROM SymposiumDetail

	symposiumId = ${symposiumId}
	and
	stype = ${stype}
	and
	lang = '${lang}'

mapper.update:
UPDATE PhotoInfo

	symposiumDetailId =
	 
		WHEN id = ${item.id} THEN ${item.boardId}
	


	id IN
	
		${item.id}
