mapper.update:
UPDATE Symposium
title = '${title}', place = '${place}', startDate = '${startDate}', finishDate = '${finishDate}', applyStart = '${applyStart}', applyFinish = '${applyFinish}'
id = ${id}

mapper.select:
SELECT * FROM Symposium
title like '%${query}%'
ORDER BY id DESC LIMIT ${from}, ${pageSize}
